import React, { useState, useEffect } from 'react';
import { Select, MenuItem, Button, Typography, TextField, Grid, FormControl, InputLabel } from '@material-ui/core';
import './Styling/SearchText.css'; // Import CSS file for styling

const SearchForText = () => {
  const [authors, setAuthors] = useState([]);
  const [works, setWorks] = useState([]);
  const [selectedAuthor, setSelectedAuthor] = useState('');
  const [selectedWork, setSelectedWork] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchAuthorsAndWorks = async () => {
      try {
        const authorsResponse = await fetch('/api/authors');
        const worksResponse = await fetch('/api/works');
        const authorsData = await authorsResponse.json();
        const worksData = await worksResponse.json();
        setAuthors(authorsData);
        setWorks(worksData);
      } catch (error) {
        console.error('Error fetching authors and works:', error);
      }
    };
    fetchAuthorsAndWorks();
  }, []);

  const handleAuthorChange = (event) => {
    setSelectedAuthor(event.target.value);
  };

  const handleWorkChange = (event) => {
    setSelectedWork(event.target.value);
  };

  const handleSearch = async () => {
    if (!selectedAuthor || !selectedWork) {
      setErrorMessage('Please select an author and a work.');
      return;
    }
    try {
      const response = await fetch(`/api/search?author=${selectedAuthor}&work=${selectedWork}`);
      const data = await response.json();
      setSearchResults(data);
      setErrorMessage('');
    } catch (error) {
      console.error('Error searching:', error);
    }
  };

  return (
    <div className="search-text-container"> {/* Apply CSS class for styling */}
      <Typography variant="h2">Search Text</Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={6}>
          <FormControl fullWidth>
            <InputLabel id="author-label">Search by Author</InputLabel>
            <Select
              labelId="author-label"
              id="author"
              value={selectedAuthor}
              onChange={handleAuthorChange}
            >
              <MenuItem value="">Select an author</MenuItem>
              {authors.map(author => (
                <MenuItem key={author.id} value={author.name}>{author.name}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <FormControl fullWidth>
            <InputLabel id="work-label">Search by Work</InputLabel>
            <Select
              labelId="work-label"
              id="work"
              value={selectedWork}
              onChange={handleWorkChange}
            >
              <MenuItem value="">Select a work</MenuItem>
              {works.map(work => (
                <MenuItem key={work.id} value={work.title}>{work.title}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <Button variant="contained" color="primary" onClick={handleSearch}>Search</Button>
          {errorMessage && <Typography className="error-message" color="error">{errorMessage}</Typography>}
        </Grid>
      </Grid>
      <div>
        {searchResults.map((result) => (
          <div key={result._id}>
            <Typography variant="h3">{result.title}</Typography>
            <Typography>{result.text}</Typography>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchForText;
